import React, { useEffect } from "react";
import {
  Link,
  useSearchParams,
  useLocation,
  useParams,
} from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Row,
  Col,
  ListGroup,
  Image,
  Form,
  Button,
  Card,
} from "react-bootstrap";
import Message from "../components/Message";
import { addToCart } from "../actions/cartactions";

function CartScreen({ histrory }) {
    const match = useParams();
  const location = useLocation();
  const productId = match.id;
  const qty = location.search ? Number(location.search.split("=")[1]) : 1;

  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  useEffect(() => {
    if (productId) {
      dispatch(addToCart(productId, qty));
    }
  }, [dispatch, productId, qty]);

  const removeFromCartHandler=()=>{


  }

  const checkoutHandler=()=>{


  }

  return (
    <Row>
      <Col md={8}>
        <h1>Shopping Cart</h1>

        {cartItems.length === 0 ? (
          <Message variant="info">
            Your cart is empty
            <Link to="/" style={{ textDecoration: "none", color: "white" }}>
              {" "}
              Go to homepage{" "}
            </Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
            {cartItems.map((item) => (
              <ListGroup.Item key={item.product}>
                <Row>
                  <Col md={3}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>

                  <Col md={5}>
                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                  </Col>

                  <Col md={2}>${item.price}</Col>
                  <Col md={3}>
                    <Form.Control
                      as="select"
                      value={item.qty}
                      onChange={(e) => dispatch(addToCart(item.product,Number(e.target.value)))}
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} vlaue={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Control>
                  </Col>

                  <Col md={1}>
                   
                   <Button type='button' variant='dark' onClick={()=>removeFromCartHandler(item.product)}>



                     <i className="fas fa-trash"></i>
                   </Button>
                  
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>

      <Col md={4}>
        <Card>


          <ListGroup variant="flush">

            <ListGroup.Item>

              <h2>Subtotal ({cartItems.reduce((acc,item)=>acc + item.qty ,0)}) items</h2>
              ${cartItems.reduce((acc,item)=>acc + item.qty * item.price ,0).toFixed(2)}
            </ListGroup.Item>
          </ListGroup>

          <ListGroup.Item>


            <Button type='button' variant='dark' className="btn-block" disabled={cartItems.length===0} onClick={checkoutHandler}>
                
                Proceed To CheckOut
          

            </Button>
          </ListGroup.Item>
        </Card>
      </Col>
    </Row>
  );
}

export default CartScreen;
